	protected boolean isStatePaidLeavePlan() {
		boolean isStatePaid = false;
		boolean isPlanSelectionOnly = false;
		final LeavePlan leavePlan = getLeavePlan();
		if (leavePlan.isPaid()) {
			final LeavePlanPaidLeave leavePlanPaidLeave = leavePlan.getLeavePlanPaidLeave();
			isStatePaid = leavePlanPaidLeave == null ? false : leavePlanPaidLeave.isStatePaid();
			if (isStatePaid) {
				isPlanSelectionOnly = leavePlanPaidLeave.isPlanSelectionOnly();
			}
		}
		return (isStatePaid &amp;&amp; isPlanSelectionOnly);
	}


